import { createSlice } from "@reduxjs/toolkit";

import { MEALS } from "../data/dummy-data";

const initialState = {
  meals: MEALS,
  filterMeals: [],
  favoriteMeals: [],
};

export const counterSlice = createSlice({
  name: "meals",
  initialState,
  reducers: {
    TOGGLE_FAVORITE: (state, action) => {
      const isIndex = state.favoriteMeals.findIndex(
        (meal) => meal.id === action.payload.mealId
      );

      if (isIndex >= 0) {
        const updatedFavMeals = [...state.favoriteMeals];
        updatedFavMeals.splice(isIndex, 1);
        return {
          ...state,
          favoriteMeals: updatedFavMeals,
        };
      } else {
        const addedMeal = state.meals.find((meal) => meal.id === action.mealId);
        return {
          ...state,
          favoriteMeals: state.favoriteMeals.concat(addedMeal),
        };
      }
    },
    FILTERED_MEALS: (state, action) => {
      state.value -= 1;
    },
    REST_FILTER: (state, action) => {
      state.value += action.payload;
    },
  },
});

// Action creators are generated for each case reducer function
export const { TOGGLE_FAVORITE, decrement, incrementByAmount } =
  counterSlice.actions;

export default counterSlice.reducer;
